import React from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";
import CustomSnackbar from '../CustomSnackbar'
import { Formik } from "formik";
import { commonStyles } from "../commonStyles";
import SaveIcon from "@material-ui/icons/Save";
import CancelIcon from "@material-ui/icons/Cancel"; 
import * as Yup from "yup";
import { format, startOfToday } from "date-fns";
import CustomCircularProgress from "../CustomCircularProgress";

const defaultDate = format(startOfToday(), 'yyyy-MM-dd')


const SaleSchema = Yup.object().shape({
  meter_type: Yup.string().trim().required("Meter Type is required"),
  prior_value: Yup.number().required("Prior Value is required").min(0,"Amount must be greater than 0"),
  sale_price: Yup.number().min(Yup.ref('prior_value'), 'Current Value must be greater than prior value').required("Current Value is required"),
  unit_charge: Yup.number().min(0,"Amount must be greater than 0").required("Unit Charge is Required"),
  base_charge: Yup.number().min(0,"Amount must be greater than 0").default(0),
  sale_date: Yup.date().required("Reading Date Required"),
  property_id: Yup.string().trim().required("Property is Required"),
  product_id: Yup.string().trim().required("Unit is Required"),
});


const SaleInputForm = ({ properties, unitsWithActiveLeases, history, meterReadingToEdit, handleItemSubmit }) => {

  const classes = commonStyles();
  const meterReadingValues = {
    id: meterReadingToEdit.id,
    property_id: meterReadingToEdit.property_id || '',
    product_id: meterReadingToEdit.product_id || '',
    sale_date: meterReadingToEdit.sale_date || defaultDate,
    prior_value: meterReadingToEdit.prior_value || '',
    sale_price: meterReadingToEdit.sale_price || '',
    base_charge: meterReadingToEdit.base_charge || '',
    unit_charge: meterReadingToEdit.unit_charge || '',
    meter_type: meterReadingToEdit.meter_type || '',
    tenant_id: meterReadingToEdit.tenant_id || '',
  }

  return (
    <Formik
      initialValues={meterReadingValues}
      enableReinitialize
      validationSchema={SaleSchema}
      onSubmit={async (values, { resetForm, setStatus }) => {
        try {
          const meterReading = {
            id: values.id,
            meter_type: values.meter_type,
            prior_value: values.prior_value,
            sale_price: values.sale_price,
            base_charge: values.base_charge,
            unit_charge: values.unit_charge,
            product_id: values.product_id,
            property_id: values.property_id,
            tenant_id: unitsWithActiveLeases.find(unit => unit.id === values.product_id).tenant_id,
            sale_date: values.sale_date,
          };
          //assign usage values to meter reading
          meterReading.usage = values.sale_price - values.prior_value
          meterReading.amount = (meterReading.usage * parseFloat(values.unit_charge)) + parseFloat(values.base_charge)
          await handleItemSubmit(meterReading, "meter_readings")
          if (!values.id) {
            const newSaleCharge = {
              charge_amount: meterReading.amount,
              charge_date: values.sale_date,
              charge_label: `${values.meter_type} meter charge`,
              charge_type: values.meter_type,
              due_date: values.sale_date,
              tenant_id: meterReading.tenant_id,
              product_id: values.product_id,
              property_id: values.property_id,
            }
            await handleItemSubmit(newSaleCharge, "transactions-charges")
          }
          resetForm({})
          if (values.id) {
            history.goBack();
          }
          setStatus({ sent: true, msg: "Meter reading and charge saved." })
        } catch (error) {
          setStatus({ sent: false, msg: `Error! ${error}.` })
        }
      }}
    >
      {({
        values,
        status,
        handleSubmit,
        setFieldValue,
        errors,
        touched,
        handleChange,
        handleBlur,
        isSubmitting,
      }) => (
        <form
          className={classes.form}
          method="post"
          id="meterInputForm"
          onSubmit={handleSubmit}
        >
          <Grid
            container
            spacing={2}
            justify="center"
            alignItems="stretch"
            direction="column"
          >
            {
              status && status.msg && (
                <CustomSnackbar
                  variant={status.sent ? "success" : "error"}
                  message={status.msg}
                />
              )
            }
            {
              isSubmitting && (<CustomCircularProgress open={true} />)
            }
            <Grid item container direction="row" spacing={2}>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  select
                  variant="outlined"
                  name="contact_id"
                  label="Customer"
                  id="contact_id"
                  onChange={(event) => {
                    setFieldValue('contact_id', event.target.value)
                    setFieldValue('contact_id', '')
                  }
                  }
                  value={values.contact_id}
                  error={errors.contact_id && touched.contact_id}
                  helperText={touched.contact_id && errors.contact_id}
                >
                  {contacts.map((contact, index) => (
                    <MenuItem key={index} value={contact.id}>
                      {contact.ref}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  select
                  variant="outlined"
                  name="product_id"
                  label="Product Name"
                  id="product_id"
                  onChange={handleChange}
                  value={values.product_id}
                  error={errors.product_id && touched.product_id}
                  helperText={touched.product_id && errors.product_id}

                >
                  {products.map((product, index) => (
                    <MenuItem key={index} value={product.id}>
                      {product.product_name}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
            </Grid>
            <Grid item>
              <TextField
                fullWidth
                type="date"
                InputLabelProps={{ shrink: true }}
                variant="outlined"
                id="sale_date"
                name="sale_date"
                label="Sale Date"
                value={values.sale_date}
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.sale_date && touched.sale_date}
                helperText={touched.sale_date && errors.sale_date}

              />
            </Grid>
            <Grid item>
              <TextField
                fullWidth
                variant="outlined"
                id="sale_price"
                name="sale_price"
                label="Sale Price"
                value={values.sale_price}
                onChange={handleChange}
                onBlur={handleBlur}
                error={errors.sale_price && touched.sale_price}
                helperText={touched.sale_price && errors.sale_price}
              />
            </Grid>
            <Grid item container className={classes.buttonBox}>
              <Grid item>
                <Button
                  color="secondary"
                  variant="contained"
                  size="medium"
                  startIcon={<CancelIcon />}
                  onClick={() => history.goBack()}
                  disableElevation
                >
                  Cancel
                  </Button>
              </Grid>
              <Grid item>
                <Button
                  type="submit"
                  color="primary"
                  variant="contained"
                  size="medium"
                  startIcon={<SaveIcon />}
                  form="meterInputForm"
                  disabled={isSubmitting}
                >
                  Save
                  </Button>
              </Grid>
            </Grid>
          </Grid>
        </form>
      )}
    </Formik>
  );
};

export default SaleInputForm;
